mapper.select:
select u.user_id,u.username,u.mobile,u.divide_per,u.create_time, u.modify_time,u.last_login_time,u.account_name,u.bank_account, u.opening_bank,u.avatar,u.description,u.status from admin_user u where 1=1
AND u.user_id = #{au.userId}
AND u.username = #{au.username}
AND u.status = #{au.status}
order by u.user_id
